import React, { useState, useEffect, Fragment } from 'react';

// This is a Hook Effect
// Effects are similar to componentDidMount and componentDidUpdate:

const Counter = props => {

  // This is a hook. Starts set to 0, modified with setCount, state viewed from count
  const [count, setCount] = useState(0);


  useEffect(() => {
    // Update the document title using the browser API
    document.title = `You clicked ${count} times`;
  });

  console.log('CounterWithEffect props:',props)

  return (
    <Fragment>
      <p>You clicked {count} times</p>

        {props.editing ? (
          <Fragment>
            <button onClick={() => setCount(count + 1)}>
              {props.name}
            </button>
          </Fragment>
        ) : (
          <Fragment>
            <button onClick={() => setCount(count + 1)}>
              Click Me
            </button>
          </Fragment>
        )}


    </Fragment>
  );
}

export default Counter
