import React, { useEffect, Fragment } from 'react';

// This is a Hook Effect
// Effects are similar to componentDidMount and componentDidUpdate:

const Counter = props => {

  // Moved this to App so that I could play with the counter's state in components above it
  // This is a hook. Starts set to 0, modified with setCount, state viewed from count
  // const [count, setCount] = useState(0);


  // When the component is mounted, changes something. componentDidMount() essentially
  useEffect(() => {
    document.title = `You clicked ${props.count} times`;  // Update the document title using the browser API
  });

  console.log('CounterWithEffect props:',props)
  console.log('Counter sets state of its own\ncount:',props)

  return (
    <Fragment>
      <p>You clicked {props.count} times</p>

        {props.editing ? (
          <Fragment>
            <button onClick={() => props.setCount(props.count + 1)}>
              {props.currentUser.name}
            </button>
          </Fragment>
        ) : (
          <Fragment>
            <button onClick={() => props.setCount(props.count + 1)}>
              Click Me
            </button>
          </Fragment>
        )}


    </Fragment>
  );
}

export default Counter
