import React, { useState, Fragment } from 'react';
import UserTable from './tables/UserTable';
import AddUserForm from './forms/AddUserForm';
import EditUserForm from './forms/EditUserForm';



const App = () => {  // Functional component doing cool class component stuff
  const usersData = [
    { id: 1, name: 'Tania', username: 'floppydiskette' },
    { id: 2, name: 'Craig', username: 'siliconeidolon' },
    { id: 3, name: 'Ben', username: 'benisphere' },
  ]
  const initialFormState = { id: null, name: '', username: '' }
  const lastUID = users[users.length - 1].id;       // users is current state of users. usersData is the initial state only

  // users and setUsers are dynamic.
  const [ users, setUsers ] = useState(usersData);  

  // This is basically 'edit mode'. Creates an 'editing' key in state.  Allows 'setEditing' to work
  const [ editing, setEditing ] = useState(false);

  const [ currentUser, setCurrentUser ] = useState(initialFormState); // Toggled update mode

  const addUser = user => {
    user.id = lastUID + 1
    setUsers([ ...users, user ])
  }

  // Set state using setUsers
  // users is current state, filter to create an array of everything except the target user
  const deleteUser = id => {
    setUsers(users.filter(user => user.id !== id));
  }

  const editUser = user => {
    setEditing(true);

    setCurrentUser({
      id:user.id,
      name:user.name,
      username:user.username
    })
  }

  const updateUser = (id, updatedUser) => {
    setEditing(false);

    setUsers(users.map(user => (user.id === id ? updatedUser : user)));
  }

  // console.log(lastUID)
  return (
    <Fragment>

      <div className="container">
          <h1>CRUD App with Hooks</h1>
          <div className="flex-row">
          <div className="flex-large">
            {editing ? (
              <div>
                <h2>Edit user</h2>
                <EditUserForm
                  editing={editing} // These two are like 
                  setEditing={setEditing} // toggle switches
                  currentUser={currentUser}
                  updateUser={updateUser}
                />
              </div>
            ) : (
              <div>
                <h2>Add user</h2>
                <AddUserForm addUser={addUser} />
              </div>
            )}
          </div>
          <div className="flex-large">
            <h2>View users</h2>
            <UserTable users={users} deleteUser={deleteUser} editUser={editUser} />
          </div>
        </div>
      </div>

    </Fragment>
  );
}

export default App;
